                <tr>
                  <td class="mono">{{ s.name }}</td>
                  <td>
                    <span class="pill {{ 'okpill' if s.active else 'dangerpill' }}">
                      {{ 'ativo' if s.active else 'inativo' }}
                    </span>
                  </td>
                  <td>
                    <span class="pill {{ 'okpill' if s.enabled else 'warnpill' }}">
                      {{ 'habilitado' if s.enabled else 'desabilitado' }}
